class.script:
    // 
var count = 0;
exports.initialize = function() {
    count = 0;
    setTimeout(null, 0);
}
exports.fire = function() {
    count = count + 1;
    if (count <= 2) {
       var value = this.get('input');
       this.send('output', value);
       this.send('input', null);
   }
}
	//
